import React, { useState } from 'react';

function AssetManagement() {
  const [assets, setAssets] = useState([]);
  const [formInput, setFormInput] = useState({
    name: '',
    type: '',
    value: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormInput({
      ...formInput,
      [name]: value,
    });
  };

  const handleAddAsset = (event) => {
    event.preventDefault();
    if (formInput.name && formInput.type && formInput.value) {
      setAssets([...assets, formInput]);
      setFormInput({
        name: '',
        type: '',
        value: '',
      });
    }
  };

  const handleRemoveAsset = (index) => {
    const newAssets = [...assets];
    newAssets.splice(index, 1);
    setAssets(newAssets);
  };

  return (
    <div>
      <h2>Asset Management</h2>

      <form onSubmit={handleAddAsset}>
        <label>
          Name:
          <input type="text" name="name" value={formInput.name} onChange={handleInputChange} />
        </label>
        <label>
          Type:
          <input type="text" name="type" value={formInput.type} onChange={handleInputChange} />
        </label>
        <label>
          Value:
          <input type="number" name="value" value={formInput.value} onChange={handleInputChange} />
        </label>
        <button type="submit">Add Asset</button>
      </form>

      <ul>
        {assets.map((asset, index) => (
          <li key={index}>
            {asset.name} ({asset.type}) - {asset.value}
            <button onClick={() => console.log(asset)}>View Details</button>
            <button onClick={() => handleRemoveAsset(index)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AssetManagement;
