// components/NewsFeed/index.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NewsFeed() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    // Fetch news related to the user's portfolio
    axios.get('https://newsapi.org/v2/everything?q=stocks OR equities OR investment&sortBy=publishedAt&language=en&apiKey=edb22bc3c9fc4674b7e8945c712b4e38')
      .then(response => {
        setNews(response.data.articles);
      })
      .catch(error => {
        console.error("Error fetching news", error);
      });
  }, []);

  return (
    <div>
      <h2>Newsfeed</h2>
      {news.map((article, index) => (
        <div key={index}>
          <h3>{article.title}</h3>
          <p>{article.description}</p>
          <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
        </div>
      ))}
    </div>
  );
}

export default NewsFeed;
