import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Make sure you have a CSS file for styling the Navbar

function Navbar() {
  const [isVisible, setIsVisible] = useState(true);
  let lastScrollTop = 0;

  useEffect(() => {
    function handleScroll() {
      let st = window.pageYOffset || document.documentElement.scrollTop;
      if (st > lastScrollTop) {
        setIsVisible(false);
      } else {
        setIsVisible(true);
      }
      lastScrollTop = st <= 0 ? 0 : st;
    }

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <nav style={{ top: isVisible ? '0' : '-60px', transition: 'top 0.3s' }} className="navbar">
      <div className="navbar-content">
        <div className="logo">
          <Link to="/" className="logo-text">Finance Diary</Link>
        </div>
        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/dashboard">Dashboard</Link>
          <Link to="/assetmanagement">Asset Management</Link>
          <Link to="/newsfeed">Newsfeed</Link>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
